        // ÂàõÂª∫ ScrollView Âπ∂ÈÖçÁΩÆÂ•Ω Delegate Ëøô‰∫õÔºåÂπ∂‰∏îÈÖçÁΩÆÂ•ΩÂàÜÈ°µ
        [self JWinitScrollView];
        [self JWinitBottomBar];
        
        for (NSInteger i = 0; i < (PREPARE_PAGE_NUMBER) / 2; i++)
        {
            [self JWloadScrollViewWithPage:i];
        }
    }
    return self;
}

